import React, { Component } from "react";
import {
  Col,
  Modal,
  Form,
  InputGroup,
  FormControl,
  Button,
} from "react-bootstrap";
import CommentList from "./CommentList";

class Movie extends Component {
  state = {
    selected: false,
    newComment: {
      comment: "",
      rate: 0,
      elementId: this.props.data.imdbID,
    },
  };

  submitComment = async (e) => {
    e.preventDefault();
    const commentsUrl = "https://striveschool.herokuapp.com/api/comments/";
    const response = await fetch(commentsUrl, {
      method: "POST",
      body: JSON.stringify(this.state.newComment),
      headers: {
        "Authorization": "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZmI2NzAxZTk4MzViMDAwMTc1ODRlZjAiLCJpYXQiOjE2MDU3OTE3NzQsImV4cCI6MTYwNzAwMTM3NH0.-0vWsUEx5v-kF_LhWXdQJ_eFcmASdAyALwPqcgnFYe8",
        // Authorization: 'Basic '+ btoa('user26:Q2h7j3FtxhdxMdab'),
        "Content-Type": "application/json",
      },
    });
    if (response.ok) {
      alert("Comment added");
      this.setState({
        newComment: {
          comment: "",
          rate: 0,
          elementId: this.props.data.imdbID,
        },
      });
    } else {
      alert("An error has occurred");
    }
  };

  handleRadioChange = (e) => {
    let newComment = this.state.newComment;
    newComment.rate = e.currentTarget.id;
    this.setState({ newComment });
  };

  handleCommentText = (e) => {
    let newComment = this.state.newComment;
    newComment.comment = e.currentTarget.value;
    this.setState({ newComment });
  };

  render() {
    return (
      <Col className="mb-2" key={this.props.data.imdbID}>
        <img
          className="img-fluid"
          src={this.props.data.Poster}
          alt="movie"
          onClick={() => {
            this.setState({ selected: !this.state.selected });
            this.props.fetchComments(this.props.data.imdbID);
          }}
        />
        <Modal
          show={this.state.selected}
          onHide={() => this.setState({ selected: !this.state.selected })}
        >
          <Modal.Header closeButton>
            <Modal.Title>Movie comments</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div className="my-3">
              {this.props.comments.length > 0 &&
                this.props.comments[0].elementId === this.props.data.imdbID && (
                  <CommentList comments={this.props.comments} />
                )}
              <div className="text-center">
                <h5 className="my-3">Add a comment</h5>
                <Form onSubmit={this.submitComment}>
                  <div className="my-3 text-center">
                    <Form.Check
                      inline
                      label="1"
                      type="radio"
                      id="1"
                      name="rating"
                      onClick={this.handleRadioChange}
                    />
                    <Form.Check
                      inline
                      label="2"
                      type="radio"
                      id="2"
                      name="rating"
                      onClick={this.handleRadioChange}
                    />
                    <Form.Check
                      inline
                      label="3"
                      type="radio"
                      id="3"
                      name="rating"
                      onClick={this.handleRadioChange}
                    />
                    <Form.Check
                      inline
                      label="4"
                      type="radio"
                      id="4"
                      name="rating"
                      onClick={this.handleRadioChange}
                    />
                    <Form.Check
                      inline
                      label="5"
                      type="radio"
                      id="5"
                      name="rating"
                      onClick={this.handleRadioChange}
                    />
                  </div>
                  <InputGroup className="mb-3">
                    <FormControl
                      placeholder="Write your comment"
                      aria-label="comment"
                      aria-describedby="basic-addon1"
                      onChange={this.handleCommentText}
                      value={this.state.newComment.comment}
                    />
                  </InputGroup>
                  <Button variant="primary" type="submit">
                    Submit
                  </Button>
                </Form>
              </div>
            </div>
          </Modal.Body>
        </Modal>
      </Col>
    );
  }
}

export default Movie;
